    private void b9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_b9ActionPerformed
    //9 button code
    String s=tI.getText();
    long l=s.length();
    int as=0;
    for(int i=0;i<l;i++)
    {char ch=s.charAt(i);
     if(ch=='.'||ch=='*'||ch=='/'||ch=='+'||ch=='-')
     as++;    
    }
    if(as==0)      
    {double sq=Double.parseDouble(tI.getText());
        if(sq==0)
        {tI.setText("");}}
    
    tI.setText(tI.getText()+9);
    }//GEN-LAST:event_b9ActionPerformed

