    private void bPtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bPtActionPerformed
    //decimal button code
    String s=tI.getText(),s1="";
    int l=s.length();
    int as=0,ad=0,af=0;
    for(int i=0;i<l;i++)
    {
     char ch=s.charAt(i);
     if(ch=='*'||ch=='/'||ch=='+'||ch=='-')
     {
      as++;
      s1=s.substring(i+1,l);
     }
     if(ch=='.')
     ad++;
    }
    if(as==0&&ad==0)
    tI.setText(tI.getText()+'.');
    else
    {
      int l1=s1.length();
      for(int i=0;i<l1;i++)
      {
       char ch=s1.charAt(i);
       if(ch=='.')
       af++;    
      }
      if(af==0&&l1>0)
      tI.setText(tI.getText()+'.');
    }
    }//GEN-LAST:event_bPtActionPerformed

    private void bRActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bRActionPerformed
    //Reciprocal button code
    String s=tI.getText(); 
    String st=s;
    int l=s.length(); 
    String s1="";
    String s2="";
    int as=0,ad=0,af=0; 
    char ch1=' ';
    double qq=0;
    char ch2=s.charAt(0);
    if(ch2=='-')
    {
     s=s.substring(1);
     l=s.length();
     af++;
     tI.setText(s);
    }
    for(int i=0;i<l;i++)
    {
     char ch=s.charAt(i);
     if(ch=='+'||ch=='-'||ch=='/'||ch=='*')
     {
      as++;
     }
    }
    char chp=s.charAt(l-1);
    if(chp=='.' || chp=='-' || chp=='+' || chp=='/' || chp=='*')
    {
     ad++;
    }
  
     if((as>0)&&(ad==0))
     {
      for(int i=0;i<l;i++)
      {
       char ch=s.charAt(i);
       if(ch=='+'||ch=='-'||ch=='/'||ch=='*')
       {
        s1=s.substring(0,i);
        s2=s.substring(i+1);
        ch1=ch;
       }
      }
      if(ch1=='+')
      { 
       qq=Double.parseDouble(s1)+Double.parseDouble(s2);
       tI.setText(qq+""); 
      }
      if(ch1=='-')
      { 
       qq=Double.parseDouble(s1)-Double.parseDouble(s2);
       tI.setText(qq+""); 
      }
      if(ch1=='/')
      { 
       qq=Double.parseDouble(s1)/Double.parseDouble(s2);  
       tI.setText(qq+""); 
      }
      if(ch1=='*')
      { 
       qq=Double.parseDouble(s1)*Double.parseDouble(s2);  
       tI.setText(qq+""); 
      }
     }
    if(ad==0)
    {
     double d=Double.parseDouble(tI.getText());
     if(af>0)
     {
      d=0-d;
     }
     d=1/d;
     tO.setText(d+"");
    }
    else
    {
     tO.setText("Error");
     tI.setText(st);
    }
    }//GEN-LAST:event_bRActionPerformed

