    private void bBSActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bBSActionPerformed
    //Backspace button code
    String s=tI.getText();
    long l=s.length();
     String s1="";
     for(int i=0;i<(l-1);i++)
     {
      char ch=s.charAt(i);
      s1=s1+ch;   
     }
     tI.setText(s1);
    if(l==1)
    tI.setText("0");   
   
    }//GEN-LAST:event_bBSActionPerformed

    private void bEqActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bEqActionPerformed
    //equals to button code
    String s=tI.getText();
    String s1="";
    String s2="";
    int as=0,ad=0; 
    char ch1=' ';
    double qq=0,out=0;
    int l=s.length();
    char ch2=s.charAt(0);
    if(ch2=='-')
    {
     s=s.substring(1);
     l=s.length();
     ad++;
    }
    for(int i=0;i<l;i++)
    {
     char ch=s.charAt(i);
     if(ch=='*'||ch=='/'||ch=='+'||ch=='-')
     as++;    
    }
    char chp=s.charAt(l-1);
    if(chp=='*' || chp=='/' || chp=='+' || chp=='-')
    {
     tO.setText("Error");
    }
    if(chp!='*' && chp!='/' && chp!='+' && chp!='-' && chp!='.')
    {
    if(as==0)
    {
     out=Double.parseDouble(s);   
    }
    if(as>0)    
    
    {
     for(int i=0;i<l;i++)
     {
      char ch=s.charAt(i);
      if(ch=='+'||ch=='-'||ch=='/'||ch=='*')
      {
       s1=s.substring(0,i);
       s2=s.substring(i+1);
       ch1=ch;
      }
     } 
     if(ch1=='+')
     {
      qq=Double.parseDouble(s1)+Double.parseDouble(s2);
     }
     if(ch1=='-')
     {
      qq=Double.parseDouble(s1)-Double.parseDouble(s2);
     }
     if(ch1=='/')
     {
      qq=Double.parseDouble(s1)/Double.parseDouble(s2);  
     }
     if(ch1=='*')
     {
      qq=Double.parseDouble(s1)*Double.parseDouble(s2); 
     } 
     out=qq;
     if(ad>0)
    {
     out=0-out;   
    }
    tO.setText(out+"");
    }
    }
    
    }//GEN-LAST:event_bEqActionPerformed

